mapper.update:
update product
set
series_id = #{seriesId}, name = #{name}, name_zh = #{nameZh}, name_en = #{nameEn},
release_date = #{releaseDate}, classification = #{classification}, added_time = #{addedTime},
edited_time = #{editedTime}, description = #{description}, remark = #{remark}, images = #{images}
where id = #{id}
